import { Address, Cell, Transaction } from "@ton/core";
import { UnknownAction } from "../../common/types";
import { NftRoyaltyParams } from "./NftRoyaltyParams";
export type SbtMintItemAction = {
    kind: 'mint';
    index: bigint;
    owner: Address;
    content: Cell;
    authority: Address | null;
    transaction: Transaction;
};
export type SbtMintBatchAction = {
    kind: 'mint_batch';
    items: {
        index: bigint;
        owner: Address;
        content: Cell;
        authority: Address | null;
    }[];
    transaction: Transaction;
};
export type SbtCollectionChangeAdminAction = {
    kind: 'change_owner';
    newOwner: Address;
    transaction: Transaction;
};
export type SbtCollectionChangeContentAction = {
    kind: 'change_content';
    newContent: Cell;
    newRoyalty: NftRoyaltyParams;
    transaction: Transaction;
};
export type SbtCollectionAction = SbtMintItemAction | SbtMintBatchAction | SbtCollectionChangeAdminAction | SbtCollectionChangeContentAction | UnknownAction;
export declare function parseSbtCollectionTransaction(tx: Transaction): SbtCollectionAction;
